          strsql  := strsql || ' TtlNb=''' || TtlNb_in || '''';
          conFlag := 2;
        elsif (conFlag = 2) then
          strsql := strsql || ', TtlNb=''' || TtlNb_in || '''';
      end if;
    end if;

   if (StartNb_in is not NULL and trim(StartNb_in) is not null) then
      if (conFlag = 1) then
          strsql  := strsql || ' StartNb=''' || StartNb_in || '''';
          conFlag := 2;
        elsif (conFlag = 2) then
          strsql := strsql || ', StartNb=''' || StartNb_in || '''';
