prompt
create or replace procedure hvps.CNAPS712801(OrnglNbOfTxs_in     in varchar2, -- 回执明细业务成功总笔数
                                      OrnglCtrlSum_in   in varchar2, -- 回执明细业务成功总金额
                                      MsgId_in in varchar2, -- 报文标识号
                                      MsgTpCd_in  in varchar2, -- 报文类型编码
                                      InstgDrctPty_in     in varchar2, -- 发起直接参与机构
                                      PrcSts_in       in varchar2, -- 处理状态
                                      NetgDt_in   in varchar2, -- 轧差日期
                                      NetgRnd_in    in varchar2, -- 轧差场次
                                      SttlmDt_in  in varchar2, -- 清算日期
                                      RecptMsgTpCd_in     in varchar2, -- 回执报文类型编码
                                      RecptMsgId_in     in varchar2, -- 回执报文标识号
                                      RecptCreDtTm_in       in varchar2 -- 回执报文发送时间
                                      ) is
  strsql  varchar2(1000);
  conFlag int;

begin
  conFlag := 1; --是不是第一位置
  strsql  := 'update BEPSMSGRGSTR set ';
  -- 回执明细业务成功总笔数
  if (OrnglNbOfTxs_in is not NULL and trim(OrnglNbOfTxs_in) is not null) then

    if (conFlag = 1) then
      strsql  := strsql || ' OrnglNbOfTxs=''' || OrnglNbOfTxs_in || '''';
      conFlag := 2; --是第二位置或更靠后
    end if;

  end if;
  -- 回执明细业务成功总金额
  if (OrnglCtrlSum_in is not NULL and trim(OrnglCtrlSum_in) is not null) then
      if (conFlag = 1) then
          strsql  := strsql || ' OrnglCtrlSum=''' || OrnglCtrlSum_in || '''';
          conFlag := 2;
        elsif (conFlag = 2) then
          strsql := strsql || ', OrnglCtrlSum=''' || OrnglCtrlSum_in || '''';
      end if;
    end if;
    --处理状态
    if (PrcSts_in is not NULL and trim(PrcSts_in) is not null) then
      if (conFlag = 1) then
        strsql := strsql || ' PrcSts=''' || PrcSts_in || '''';
        conFlag := 2;
        elsif (conFlag = 2) then
        strsql := strsql || ', PrcSts=''' || PrcSts_in || '''';
      end if;
    end if;
    -- 轧差日期
