prompt Creating procedure CNAPS990704
prompt ==============================
prompt
create or replace procedure hvps.CNAPS990704(CmonDataTp_in          in varchar2, --公共数据类型
                                        CmonDataNm_in       in varchar2, --公共数据名称
                                        CmonDataVal_in       in varchar2, --公共数据值
                                        ALTSTYLE_in         in varchar2, --变更方式
                                        CmonDataCd_in        in varchar2 --公共数据代码
                                        ) is

strsql  varchar2(1000);
conFlag int;
begin

  conFlag := 1; --是不是第一位置
  strsql  := 'update ccmsSysParaInfo set ';
  if (CmonDataTp_in is not NULL and trim(CmonDataTp_in) is not null) then
    if (conFlag = 1) then
      strsql  := strsql || ' CmonDataTp=''' || CmonDataTp_in || '''';
      conFlag := 2; --是第二位置或更靠后
    end if;
  end if;

  if (CmonDataNm_in is not NULL and trim(CmonDataNm_in) is not null) then
      if (conFlag = 1) then
          strsql  := strsql || ' CmonDataNm=''' || CmonDataNm_in || '''';
          conFlag := 2;
        elsif (conFlag = 2) then
          strsql := strsql || ', CmonDataNm=''' || CmonDataNm_in || '''';
      end if;
    end if;


    if (CmonDataVal_in is not NULL and trim(CmonDataVal_in) is not null) then
      if (conFlag = 1) then
          strsql  := strsql || ' CmonDataVal=''' || CmonDataVal_in || '''';
          conFlag := 2;
        elsif (conFlag = 2) then
          strsql := strsql || ', CmonDataVal=''' || CmonDataVal_in || '''';
      end if;
    end if;

    if (ALTSTYLE_in is not NULL and trim(ALTSTYLE_in) is not null) then
      if (conFlag = 1) then
          strsql  := strsql || ' ALTSTYLE=''' || ALTSTYLE_in || '''';
          conFlag := 2;
        elsif (conFlag = 2) then
          strsql := strsql || ', ALTSTYLE=''' || ALTSTYLE_in || '''';
      end if;
    end if;

  strsql:=strsql||'  where CmonDataCd='''||trim(CmonDataCd_in)||'''';
    dbms_output.put_line(strsql);
    execute immediate strsql;
end;
/

