    strsql  varchar2(500);
    conFlag int;
begin
   conFlag :=0;
   strsql:='update CNAPSPMTCOLLECT  ';
   
   if (RecptMsgId_in is not NULL  and trim(RecptMsgId_in) is not null) then
   
       strsql:=strsql||' set RecptMsgId='''||RecptMsgId_in||'''';
