          conFlag:=1;
       
     
      ELSIF  (conFlag=1) then
          strsql:=strsql||',Status='''||trim(Status_in)||'''';
       
       end if;
      
   end if;
   
   
   if (NetgDt_in is not NULL  and trim(NetgDt_in) is not null) then
       if (conFlag=0) then
          strsql:=strsql||' set NetgDt='''||trim(NetgDt_in)||'''';
          conFlag:=1;
     
       ELSIF (conFlag=1) then
          strsql:=strsql||',NetgDt='''||trim(NetgDt_in)||'''';
       
       end if;
      
   end if;
   
   
   if (NetgRnd_in is not NULL   and trim(NetgRnd_in) is not null ) then
       if (conFlag=0) then
          strsql:=strsql||' set NetgRnd='''||trim(NetgRnd_in)||'''';
