       ELSIF (conFlag=1) then
          strsql:=strsql||',NetgRnd='''||trim(NetgRnd_in)||'''';
       
       end if;
      
   end if;
   
   
   if (SttlmDt_in is not NULL   and trim(SttlmDt_in) is not null ) then
       if (conFlag=0) then
          strsql:=strsql||' set SttlmDt='''||trim(SttlmDt_in)||'''';
