prompt
CREATE OR REPLACE PROCEDURE HVPS.valinsert (
   colstr      IN       VARCHAR2,                                   --字段名称
   valstr      IN       VARCHAR2,                                     --字段值
   tablename   IN       VARCHAR2,                                     --表名称
   seqstr      IN       VARCHAR2,                                   --所用序列
   RESULT      OUT      VARCHAR2                                        --返回
)
IS
   TYPE values_arrary IS TABLE OF VARCHAR2 (30)
      INDEX BY BINARY_INTEGER;                                   --字段值数组

   v_arrary      values_arrary;
   v_coun        NUMBER (3);                                       --临时变量
   v_count       NUMBER (3);                                       --统计字符串中逗号个数                            
   v_value       VARCHAR2 (1000);                                  --临时变量
   v_temp        VARCHAR2 (200);                                   --临时变量
   v_length      NUMBER (3);
   v_sqlstring   VARCHAR2 (100);                              --执行的sql语句
BEGIN
--获得字符串长度
   v_length := LENGTH (colstr);

   --取出值个数
   FOR v_coun IN 1 .. v_length
   LOOP
      IF (INSTR (colstr, ',', 1, v_coun) = 0)
      THEN
         v_count := v_coun;
         EXIT;
      END IF;
   END LOOP;

  -- DBMS_OUTPUT.put_line (v_count);

   --v_count=1代表一个值，无逗号
   IF v_count = 1
   THEN
      v_arrary (1) := chr(39)||valstr||chr(39);
      v_sqlstring :=
            'insert into '
         || TRIM (tablename)
         || '('
         || colstr
         || ') values ('
         || v_arrary (1)
         || ')';

      EXECUTE IMMEDIATE v_sqlstring;
   END IF;

   IF v_count > 1
   THEN
      --取出第一个逗号前的字符串
      v_arrary (1) := TRIM (SUBSTR (valstr, 1, INSTR (valstr, ',', 1, 1) - 1));
      v_value := CHR (39) || v_arrary (1) || CHR (39);

      --迭代取出每个逗号后的字符串，最后一个逗号后的字符串不取
      FOR v_coun IN 2 .. v_count - 1
      LOOP
         v_arrary (v_coun) :=
            SUBSTR (valstr,
                    INSTR (valstr, ',', 1, v_coun - 1) + 1,
                      INSTR (valstr, ',', 1, v_coun)
                    - INSTR (valstr, ',', 1, v_coun - 1)
                    - 1
                   );
         v_value :=
               v_value
            || CHR (44)
            || CHR (39)
            || TRIM (v_arrary (v_coun))
            || CHR (39);
      END LOOP;

      --取出最后一个逗号后的字符串
      v_arrary (v_count) :=
             SUBSTR (valstr, INSTR (valstr, ',', 1, v_count - 1) + 1,
                     v_length);
      v_value :=
         v_value || CHR (44) || CHR (39) || TRIM (v_arrary (v_count))
         || CHR (39);
      v_sqlstring :=
            'insert into '
         || TRIM (tablename)
         || '('
         || colstr
         || ') values ('
         || v_value
         || ')';
      DBMS_OUTPUT.put_line (v_sqlstring);

      EXECUTE IMMEDIATE v_sqlstring;
   END IF;
END valinsert;
/

