mapper.select:
SELECT t.id, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.is_extend as isExtend, t.create_date as createDate, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.update_date as updateDate, t.delflag as delflag, t.concurrency_version as concurrencyVersion, t.code as code, t.filed_name as filedName, t.content_type_id as contentTypeId, t.type as type, t.sort_desc as sortDesc, t.view_name as viewName, t.is_view as isView, t.column_with as columnWith, t.is_required as isRequired, t.sort as sort, t.tend_id as tendId, t.is_line as isLine FROM oa_content_row_attribute t where t.delflag=0
AND content_type_id = #{contentTypeId,jdbcType=VARCHAR}
and (t.code like CONCAT(CONCAT('%',#{code}),'%') or t.filed_name like CONCAT(CONCAT('%',#{filedName}),'%') )
limit #{start},#{limit}

SELECT count(t.id) FROM oa_content_row_attribute t where t.delflag=0
AND content_type_id = #{contentTypeId,jdbcType=VARCHAR}
and (t.code like CONCAT(CONCAT('%',#{code}),'%') or t.filed_name like CONCAT(CONCAT('%',#{filedName}),'%') )
