mapper.select:
		 select sum(file_size) as totalFileSize,count_capacity as countCapacity from oa_content_type where 1=1

    		AND content_type = #{contentType,jdbcType=VARCHAR}

		 group by content_type

SELECT t.id, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.create_date as createDate, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.update_date as updateDate, t.delflag as delflag, t.concurrency_version as concurrencyVersion, t.name as name, t.publish_state as publishState, t.code as code, t.view_sort as viewSort, t.content_type as contentType, t.count_capacity as countCapacity, t.file_size as fileSize, t.business_object as businessObject, t.description as description, t.tend_id as tendId FROM oa_content_type t where 1=1
and (t.code like CONCAT(CONCAT('%',#{code}),'%') or t.name like CONCAT(CONCAT('%',#{name}),'%') )
limit #{start},#{limit}
