mapper.select:
SELECT t.id, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.create_date as createDate, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.update_date as updateDate, t.delflag as delflag, t.concurrency_version as concurrencyVersion, t.batch_id as batchId, t.stock_info_id as stockInfoId, t.stock_num as stockNum, t.stock_name as stockName, t.stock_specifications as stockSpecifications, t.metering_unit as meteringUnit, t.stock_brand as stockBrand, t.in_count as inCount, t.buy_price as buyPrice, t.count_money as countMoney, t.buy_date as buyDate, t.tend_id as tendId, t.house_id as houseId, h.type_name as typeName FROM oa_office_record t LEFT JOIN oa_office_house h ON t.house_id=h.id where t.delflag=0
and h.type_name like CONCAT(CONCAT('%',#{houseName}),'%')
and t.house_id =#{houseId}
and t.stock_info_id =#{stockInfoId}
and t.stock_name like CONCAT(CONCAT('%',#{stockName}),'%')
and t.stock_num like CONCAT(CONCAT('%',#{stockNum}),'%')
limit #{start},#{limit}

SELECT count(t.id) FROM oa_office_record t LEFT JOIN oa_office_house h ON t.house_id=h.id where t.delflag=0
and h.type_name like CONCAT(CONCAT('%',#{houseName}),'%')
and t.house_id =#{houseId}
and t.stock_info_id =#{stockInfoId}
and t.stock_name like CONCAT(CONCAT('%',#{stockName}),'%')
and t.stock_num like CONCAT(CONCAT('%',#{stockNum}),'%')
