CREATE  VIEW `view_post_dto` AS select `p`.`id` AS `id`,`p`.`code` AS `code`,`r`.`name` AS `name`,`oo`.`prefix_id` AS `orgPrefixId`,`oo`.`prefix_name` AS `orgPrefixName`,`r`.`id` AS `roleId`,`r`.`type` AS `roleTypeId`,`p`.`ref_id` AS `refId`,`p`.`leader_id` AS `leaderId`,`p`.`create_person_id` AS `createPersonId`,`p`.`create_person_name` AS `createPersonName`,`p`.`create_org_id` AS `createOrgId`,`p`.`create_org_name` AS `createOrgName`,`p`.`create_company_id` AS `createCompanyId`,`p`.`create_company_name` AS `createCompanyName`,`p`.`create_date` AS `createDate`,`p`.`update_person_id` AS `updatePersonId`,`p`.`update_person_name` AS `updatePersonName`,`p`.`update_date` AS `updateDate`,`p`.`delflag` AS `delflag`,`p`.`concurrency_version` AS `concurrencyVersion`,`p`.`type` AS `type`,`p`.`role_id` AS `role_id`,`p`.`tend_id` AS `tend_id`,`p`.`sort` AS `sort`,`p`.`incon` AS `incon`,`p`.`status` AS `status` from ((`pt_sys_org_post` `p` left join `pt_sys_org_standard_role` `r` on((`p`.`role_id` = `r`.`id`))) left join `pt_sys_org_orgnazation` `oo` on((`oo`.`id` = `p`.`ref_id`))) where (`p`.`delflag` = 0) ;