mapper.select:
SELECT r.concurrency_version as concurrencyVersion, r.create_company_id as createCompanyId, r.create_company_name as createCompanyName, r.create_date as createDate, r.create_org_id as createOrgId, r.create_org_name as createOrgName, r.create_person_id as createPersonId, r.create_person_name as createPersonName, r.delflag, r.fi_sys_code as fiSysCode, r.fi_sys_name as fiSysName, r.id, r.sender, r.status, r.tend_id as tendId, r.type, r.update_date as updateDate, r.update_person_id as updatePersonId, r.update_person_name as updatePersonName, r.web_url as webUrl FROM pt_fi_sys_register r where r.delflag=0
and r.status=#{status}
and r.fi_sys_name like CONCAT(CONCAT('%',#{sysName}),'%')
order by r.create_date desc
limit #{start},#{limit}

select count(r.id) FROM pt_fi_sys_register r where r.delflag=0
and r.status=#{status}
and r.fi_sys_name like CONCAT(CONCAT('%',#{sysName}),'%')
