mapper.select:
SELECT id AS id, concurrency_version AS concurrencyVersion, CODE AS CODE, NAME AS NAME, tend_id AS tendId, icon AS icon, STATUS AS STATUS, sort AS sort, is_permission AS isPermission, full_name AS fullName, url AS url, isextsys AS isextsys, openmode AS openmode, remark AS remark, create_date AS createDate, update_date AS updateDate, create_person_id AS createPersonId, create_person_name AS createPersonName, update_person_id AS updatePersonId, update_person_name AS updatePersonName, create_org_id AS createOrgId, create_org_name AS createOrgName, create_company_id AS createCompanyId, create_company_name AS createCompanyName, delflag AS delflag FROM PT_SYS_RES_APP WHERE 1=1
and status=#{appStatus}
and code =#{code}
and delflag = #{appDelFlag}
and id = #{appId}
ORDER BY sort
