mapper.select:
select s.id as id, s.create_person_id as createPersonId, s.create_person_name as createPersonName, s.create_org_id as createOrgId, s.create_org_name as createOrgName, s.create_company_id as createCompanyId, s.create_company_name as createCompanyName, s.create_date as createDate, s.update_person_id as createPersonId, s.update_person_name as updatePersonName, s.update_date as updateDate, s.delflag as delflag, s.concurrency_version as concurrencyVersion, s.tend_id as tendId, s.form_format_json as formFormatJson, s.form_value_json as formValueJson, s.custom_form_id as customFormId, s.form_search_key as formSearchKey, s.status, s.form_flow_variable_value as formFlowVariableValue, s.operator_id as operatorId, s.operator_name as operatorName, s.operate_department_id as operateDepartmentId, s.operate_department_name as operateDepartmentName, s.operate_company_id as operateCompanyId, s.operate_company_name as operateCompanyName, s.operate_project_id as operateProjectId, s.operate_project_name as operateProjectName, s.operate_qi_id as operateQiId, s.operate_qi_name as operateQiName, s.instance_id as instanceId, s.form_search_senior_value as formSearchSeniorValue from pt_sys_base_custom_form_instance s where s.delflag='0'
and s.custom_form_id =#{customFormId}
${formSearchKey}
${formSearchSeniorValue}
order by s.create_date desc limit #{start},#{limit}

	select 
		count(1) 
		from pt_sys_base_custom_form_instance s where s.delflag='0'

          and  s.custom_form_id =#{customFormId}


          ${formSearchKey} 


          ${formSearchSeniorValue}

mapper.update:
	update pt_sys_base_custom_form_instance set status = #{status}

         , instance_id = #{instanceId}


         , form_value_json = #{formValueJson}


         , form_mobile_value_json = #{formMobileValueJson}

	 where id = #{id}
