mapper.select:
select s.id as id, s.create_person_id as createPersonId, s.create_person_name as createPersonName, s.create_org_id as createOrgId, s.create_org_name as createOrgName, s.create_company_id as createCompanyId, s.create_company_name as createCompanyName, s.create_date as createDate, s.update_person_id as createPersonId, s.update_person_name as updatePersonName, s.update_date as updateDate, s.delflag as delflag, s.concurrency_version as concurrencyVersion, s.code, s.name, s.parent_id as parentId, t.name as parentName, s.biz_flow_code as bizFlowCode, s.biz_flow_name as bizFlowName, s.flow_path_id as flowPathId, s.flow_path_name as flowPathName, s.flow_path_show_name as flowPathShowName, s.default_flow_id as defaultFlowId, s.default_flow_name as defaultFlowName, s.sort, s.form_format_json as formFormatJson, s.form_format_html as formFormatHtml, s.flow_template_id as flowTemplateId, s.flow_template_name as flowTemplateName, s.tend_id as tendId, s.form_show_column as formShowColumn, s.form_search_key as formSearchKey, s.is_inner as isInner, s.resource_id as resourceId, s.resource_name as resourceName, s.url, s.status from pt_sys_base_custom_form s left join pt_sys_base_custom_form_group t on s.parent_id = t.id where s.delflag='0' and t.delflag='0'
and s.parent_id =#{parentId}
order by s.sort limit #{start},#{limit}

select count(1) from pt_sys_base_custom_form s left join pt_sys_base_custom_form_group t on s.parent_id = t.id where s.delflag='0' and t.delflag='0'
and s.parent_id =#{parentId}
order by s.sort


		SELECT count(1) FROM pt_sys_base_custom_form s
		where s.delflag='0' 

            and  s.code = #{code,jdbcType=VARCHAR}  


            and  s.id != #{id,jdbcType=VARCHAR}


		SELECT count(1) FROM pt_sys_base_custom_form s 
		where s.delflag='0'  

            and  s.name = #{name,jdbcType=VARCHAR}  


            and  s.id != #{id,jdbcType=VARCHAR}  


            and  s.parent_id = #{parentId,jdbcType=VARCHAR}
