mapper.select:
select t.id as id , t.create_date as createDate, t.update_date as updateDate, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.concurrency_version as concurrencyVersion, t.delflag as delflag, t.name as name, t.code as code, t.status as status, t.remark as remark from pt_sys_settlement_trades t where t.delflag=0
and t.name LIKE CONCAT(CONCAT('%',#{name}),'%')
and t.status=#{status}
order by t.code
limit #{start},#{limit}

select count(t.id) from  pt_sys_settlement_trades t where t.delflag=0 

		  and t.name  LIKE  CONCAT(CONCAT('%',#{name}),'%')


		  and t.code  =#{code}


		  and t.status=#{status}
