mapper.select:
		SELECT * FROM
		oa_bbs_forum_type t

        	 t.delflag = 0 and
			 t.code = #{code}

			  and t.id != #{id}

select k.id,k.name,k.code,k.pId,k.dataType from ( (SELECT s.id, s.name ,s.code,s.parent_id as pId,'0' as dataType,s.sort_num,s.create_date FROM oa_bbs_forum_type s where s.delflag='0' ORDER BY s.sort_num,s.create_date) union (select b.id,b.name,'' as code,b.type_id as pId,'1' as dataType,b.sort_num,b.create_date from oa_bbs_forum b where b.delflag='0' and b.id in(
select forum_id from oa_bbs_forum_permission where delflag='0' and role_id in
#{item}
UNION
select id from oa_bbs_forum where id not in(select forum_id from oa_bbs_forum_permission) ) ORDER BY b.sort_num,b.create_date) ) k ORDER BY k.sort_num,k.create_date;

