mapper.select:
SELECT t.id, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.create_date as createDate, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.update_date as updateDate, t.delflag as delflag, t.concurrency_version as concurrencyVersion, t.tend_id as tendId, t.title as title, t.title_pic as titlePic, t.code as code, t.doc_content as docContent, t.author_id as authorId, t.author as author, t.importance as importance, t.keywords as keywords, t.doc_desc as docDesc, t.publish_date as publishDate, t.status as status, t.hit_num as hitNum, t.belong_dept_id as belongDeptId, t.belong_dept_name as belongDeptName, t.belong_company_id as belongCompanyId, t.belong_company_name as belongCompanyName, t.content_type_id as contentTypeId, t.content_child_id as contentChildId, t.stick as stick, t.stick_cycle as stickCycle, t.stick_invalid_time as stickInvalidTime, t.sort_num as sortNum, t.process_instance_id as processInstanceId, t.business_object_code as businessObjectCode, t.fl_code as flCode, t.relation_id as relationId, t.big_version as bigVersion, t.minor_version as minorVersion, ct.approval_process as approvalProcess, cc.name as contentChildName, ct.name as contentTypeName FROM oa_content_row_type t LEFT JOIN oa_content_child cc ON t.content_child_id = cc.id LEFT JOIN oa_content_type ct on t.content_type_id = ct.id where t.delflag=0
AND t.content_type_id = #{contentTypeId,jdbcType=VARCHAR}
AND (t.content_child_id = #{contentChildId,jdbcType=VARCHAR} or cc.parent_id = #{contentChildId,jdbcType=VARCHAR})
AND t.publish_state = #{publishState,jdbcType=VARCHAR}
AND t.title = #{title}
AND t.author = #{author}
AND t.doc_content like '%${docContent}%'
AND t.create_person_id = #{createPersonId}
AND t.belong_dept_id = #{belongDeptId}
AND t.create_date > #{starttime}
AND t.create_date < #{endtime}
AND (t.title like '%${param}%' or t.code like '%${param}%')
ORDER BY t.stick DESC ,t.sort_num ASC , t.create_date DESC
limit #{start},#{limit}

SELECT
count(t.id)
FROM
oa_content_row_type t
LEFT JOIN
oa_content_child cc
ON t.content_child_id = cc.id
where t.delflag=0

    AND t.content_type_id = #{contentTypeId,jdbcType=VARCHAR}


    AND (t.content_child_id = #{contentChildId,jdbcType=VARCHAR}
    or cc.parent_id = #{contentChildId,jdbcType=VARCHAR})



    AND t.publish_state = #{publishState,jdbcType=VARCHAR}


    AND t.title = #{title}


    AND t.author = #{author}


    AND t.doc_content like '%${docContent}%'


    AND t.create_person_id = #{createPersonId}


    AND t.belong_dept_id = #{belongDeptId}


    AND t.create_date > #{starttime}


    AND t.create_date < #{endtime}


    AND (t.title like '%${param}%' or t.code like '%${param}%')

Select id, create_person_id as createPersonId, create_person_name as createPersonName, create_org_id as createOrgId, create_org_name as createOrgName, create_company_id as createCompanyId, create_company_name as createCompanyName, create_date as createDate, update_person_id as updatePersonId, update_person_name as updatePersonName, update_date as updateDate, delflag as delflag, concurrency_version as concurrencyVersion, tend_id as tendId, title as title, title_pic as titlePic, code as code, doc_content as docContent, author_id as authorId, author as author, importance as importance, keywords as keywords, doc_desc as docDesc, publish_date as publishDate, status as status, hit_num as hitNum, belong_dept_id as belongDeptId, belong_dept_name as belongDeptName, belong_company_id as belongCompanyId, belong_company_name as belongCompanyName, content_type_id as contentTypeId, content_child_id as contentChildId, stick as stick, stick_cycle as stickCycle, stick_invalid_time as stickInvalidTime, sort_num as sortNum, process_instance_id as processInstanceId, business_object_code as businessObjectCode, relation_id as relationId, big_version as bigVersion, minor_version as minorVersion from oa_content_row_type where delflag = 0 and sort_num = (SELECT min(sort_num) from oa_content_row_type where delflag = 0
and sort_num > #{sortNum}
and content_type_id = #{contentTypeId,jdbcType=VARCHAR}
and content_child_id = #{contentChildId,jdbcType=VARCHAR}
)
and content_type_id = #{contentTypeId,jdbcType=VARCHAR}
and content_child_id = #{contentChildId,jdbcType=VARCHAR}

Select id, create_person_id as createPersonId, create_person_name as createPersonName, create_org_id as createOrgId, create_org_name as createOrgName, create_company_id as createCompanyId, create_company_name as createCompanyName, create_date as createDate, update_person_id as updatePersonId, update_person_name as updatePersonName, update_date as updateDate, delflag as delflag, concurrency_version as concurrencyVersion, tend_id as tendId, title as title, title_pic as titlePic, code as code, doc_content as docContent, author_id as authorId, author as author, importance as importance, keywords as keywords, doc_desc as docDesc, publish_date as publishDate, status as status, hit_num as hitNum, belong_dept_id as belongDeptId, belong_dept_name as belongDeptName, belong_company_id as belongCompanyId, belong_company_name as belongCompanyName, content_type_id as contentTypeId, content_child_id as contentChildId, stick as stick, stick_cycle as stickCycle, stick_invalid_time as stickInvalidTime, sort_num as sortNum, process_instance_id as processInstanceId, business_object_code as businessObjectCode, relation_id as relationId, big_version as bigVersion, minor_version as minorVersion from oa_content_row_type where delflag = 0 and sort_num = ( SELECT max(sort_num) from oa_content_row_type where delflag = 0
and sort_num < #{sortNum}
and content_type_id = #{contentTypeId,jdbcType=VARCHAR}
and content_child_id = #{contentChildId,jdbcType=VARCHAR}
)
and content_type_id = #{contentTypeId,jdbcType=VARCHAR}
and content_child_id = #{contentChildId,jdbcType=VARCHAR}

mapper.update:
UPDATE oa_content_row_type
set sort_num = #{sortNum}
WHERE id=#{id}
