mapper.select:
		SELECT
			t.user_id as userId
        FROM
			oa_meeting_partner t
		WHERE 1=1 


			AND user_identifiy not in 

	            #{item} 





				and t.user_id = #{userId,jdbcType=VARCHAR}




				and t.meeting_id = #{meetingId,jdbcType=VARCHAR}


		group by t.user_id

		SELECT

			t.user_name as userName
        FROM
			oa_meeting_partner t
		WHERE 1=1


				and t.user_id = #{userId,jdbcType=VARCHAR}




				and t.meeting_id = #{meetingId,jdbcType=VARCHAR}



			AND user_identifiy not in 

	            #{item} 




				and t.join_flag = #{joinFlag,jdbcType=VARCHAR}


		group by t.user_id

SELECT t.id, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.create_date as createDate, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.update_date as updateDate, t.delflag as delflag, t.concurrency_version as concurrencyVersion, t.meeting_id as meetingId, t.user_id as userId, t.user_name as userName, t.join_flag as joinFlag, t.reply_date as replyDate, t.meeting_state as meetingState, t.user_identifiy as userIdentifiy, t.tend_id as tendId
FROM oa_meeting_partner t WHERE 1=1
and t.user_id = #{userId,jdbcType=VARCHAR}
and t.meeting_id = #{meetingId,jdbcType=VARCHAR}
ORDER BY t.reply_date desc limit 0,1

SELECT t.id, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.create_date as createDate, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.update_date as updateDate, t.delflag as delflag, t.concurrency_version as concurrencyVersion, t.meeting_id as meetingId, t.user_id as userId, t.user_name as userName, t.join_flag as joinFlag, t.reply_date as replyDate, t.meeting_state as meetingState, t.user_identifiy as userIdentifiy, t.tend_id as tendId
FROM oa_meeting_partner t WHERE 1=1
and t.user_id = #{userId,jdbcType=VARCHAR}
and t.meeting_id = #{meetingId,jdbcType=VARCHAR}
ORDER BY t.reply_date desc

SELECT m.title as title, t.user_identifiy as userIdentifiy, s.id as meetingSummaryId FROM oa_meeting m LEFT JOIN oa_meeting_partner t ON m.id = t.meeting_id LEFT JOIN oa_meeting_summary s ON m.id = s.meeting_id WHERE 1=1
and m.id = #{meetingId,jdbcType=VARCHAR}
and t.user_id = #{userId,jdbcType=VARCHAR}
