mapper.select:
SELECT a.id as id, a.name as name, a.description as description, b.id as serveyCategoryId, b.name as serveyCategoryName, a.create_date as createDate, a.flow_status as flowStatus, a.instance_id as instanceId, CASE WHEN (a.status=0 or a.status is null or a.status = 3) then '草稿' WHEN a.status=1 THEN '审批中' WHEN (a.status=2 and a.from_date > NOW()) THEN '未开始' WHEN (a.status=2 and a.thru_date < NOW()) THEN '已结束' ELSE '进行中' END as status FROM oa_servey a left join oa_servey_category b on a.servey_category_id = b.id where 1=1
and a.servey_type = #{serveyType}
and a.name like CONCAT(CONCAT('%',#{name}),'%')
and a.servey_category_id = #{serveyCategoryId}
order by a.create_date desc
limit #{start},#{limit}

SELECT count(a.id) FROM oa_servey a left join oa_servey_category b on a.servey_category_id = b.id where 1=1
and a.servey_type = #{serveyType}
and a.name like CONCAT(CONCAT('%',#{name}),'%')
and a.servey_category_id = #{serveyCategoryId}
