mapper.select:
SELECT r.concurrency_version as concurrencyVersion, r.create_company_id as createCompanyId, r.create_company_name as createCompanyName, r.create_date as createDate, r.create_org_id as createOrgId, r.create_org_name as createOrgName, r.create_person_id as createPersonId, r.create_person_name as createPersonName, r.delflag, r.code , r.name, r.id, r.app_code as appCode, r.tend_id as tendId, r.update_date as updateDate, r.update_person_id as updatePersonId, r.update_person_name as updatePersonName, r.register_id as registerId, r.company_code as companyCode, r.company_name as companyName FROM PT_FI_ACCOUNT_SET r where r.delflag=0
and r.register_id=#{registerId}
and r.app_code=#{appCode}
order by r.create_date desc
limit #{start},#{limit}

select count(r.id) FROM PT_FI_ACCOUNT_SET r 
where r.delflag=0 

	and r.register_id=#{registerId}


and r.app_code=#{appCode}
