UPDATE pt_flow_participant 
SET participant_id = #{participantId} ,  
    participant_type=#{participantType}, 
    participant_scope=#{participantScope},
    param_value=#{paramValue} 
WHERE fl_id = #{flId} 
    AND participant_id=#{oldParticipantId} 
    AND participant_type=#{oldParticipantType} 
    AND ac_id=#{acId} 
    AND delflag=0

mapper.select:
SELECT DISTINCT -- p.id , -- p.type, -- p.fl_id AS flId, -- p.ac_id AS acId, p.participant_id AS participantId, p.participant_name AS participantName, p.participant_type AS participantType -- p.participant_scope AS participantScope, -- p.param_value AS paramValue, -- p.sort AS sort FROM PT_FLOW_PARTICIPANT p WHERE 1=1 AND p.type='1' AND p.delflag=0
AND p.ac_id IN
#{acId}

SELECT p.id , p.type, p.fl_id AS flId, p.ac_id AS acId, p.participant_id AS participantId, p.participant_name AS participantName, p.participant_type AS participantType, p.participant_scope AS participantScope, p.param_value AS paramValue, p.sort AS sort, p.tend_id AS tendId, p.create_date AS createDate, p.update_date AS updateDate, p.create_person_id AS createPersonId, p.create_person_name AS createPersonName, p.update_person_id AS updatePersonId, p.create_org_id AS createOrgId, p.create_org_name AS createOrgName, p.create_company_id AS createCompanyId, p.create_company_name AS createCompanyName, p.concurrency_version AS concurrencyVersion, p.delflag AS delflag
FROM PT_FLOW_PARTICIPANT p WHERE 1=1 AND p.type='1' AND p.delflag=0
AND p.ac_id IN
#{acId}
AND p.participant_id IN
#{participantId}
