mapper.select:
SELECT t.id as id, t.name as name, t.code as code, t.province_id as provinceId, t.city_id as cityId, t.representative as representative, t.remark as remark, t.disabled_id as disabledId, t.disabled_date as disabledDate, t.status as status, t.company_id as companyId, t.remark as remark, t.create_date as createDate, t.update_date as updateDate, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.concurrency_version as concurrencyVersion, t.delflag as delflag, c.name as companyName, r.name as provinceName, rt.name as cityName from pt_sys_base_corporation t LEFT JOIN pt_sys_org_orgnazation c ON t.company_id=c.id LEFT JOIN pt_sys_base_region r ON r.id=t.province_id LEFT JOIN pt_sys_base_region rt ON rt.id=t.city_id where t.delflag=0
and t.name like CONCAT(CONCAT('%',#{name}),'%')
and t.company_id =#{companyname}
and t.province_id =#{provicename}
order by t.create_date desc limit #{start},#{limit}

SELECT count(t.id) from pt_sys_base_corporation t LEFT JOIN pt_sys_org_orgnazation c ON t.company_id=c.id LEFT JOIN pt_sys_base_region r ON r.id=t.province_id LEFT JOIN pt_sys_base_region rt ON rt.id=t.city_id where t.delflag=0
and t.name like CONCAT(CONCAT('%',#{name}),'%')
and t.company_id =#{companyname}
and t.province_id =#{provicename}
