mapper.select:
SELECT id AS id, CODE AS CODE, NAME AS NAME, type AS type, sort AS sort, 'cata' as 'mold', parent_id AS parentId FROM pt_sys_org_role_catalog
where parent_id = #{_parameter,jdbcType=VARCHAR};
where parent_id is null OR parent_id = '';

SELECT id AS id, CODE AS CODE, NAME AS NAME, type AS type, sort AS sort, 'cata' as 'mold', status as status, prefix_id as prefixId, prefix_name as prefixName, prefix_sort as prefixSort, parent_id AS parentId FROM pt_sys_org_role_catalog
where (parent_id is null OR parent_id = '')
and `status` =#{roleCataStatus}
and delflag=#{roleCataDelFlag}
and type=#{type,jdbcType=VARCHAR}
order by sort
