mapper.select:
        select count(id) from (



		          attm.category_id in

		        #{id}      



		) count_aliase

select.where:

			attm.app_id = #{appId}


          	and attm.business_id in

		        #{id}      



		    and attm.category_id = #{categoryId}

		order by CONVERT( attm.name USING gbk ) COLLATE gbk_chinese_ci ASC

mapper.select:
SELECT attm.id, attm.name, attm.type, attm.extend_name as extendName, attm.full_name as fullName, attm.url, attm.path, attm.file_size as fileSize, attm.upload_time as uploadTime, attm.category_id as categoryId, ctgr.name as categoryName FROM pt_univ_file_attachment attm LEFT JOIN pt_univ_file_category ctgr ON attm.category_id = ctgr.id
attm.app_id = #{appId}
and attm.business_id = #{businessId}
