mapper.select:
SELECT r.concurrency_version as concurrencyVersion, r.create_company_id as createCompanyId, r.create_company_name as createCompanyName, r.create_date as createDate, r.create_org_id as createOrgId, r.create_org_name as createOrgName, r.create_person_id as createPersonId, r.create_person_name as createPersonName, r.tend_id as tendId, r.update_date as updateDate, r.update_person_id as updatePersonId, r.update_person_name as updatePersonName, r.delflag, r.enter_name as enterName, r.id, r.enter_id as enterId, r.account_set_id as accountSetId, r.enter_date as enterDate, r.export_date as exportDate, r.word , r.not_full_error as notFullError, r.send_status as sendStatus, r.error_cause as errorCause, r.voucher_no as voucherNo, r.fiscal_year as fiscalYear, r.accounting_period as accountingPeriod, r.attachment_number as attachmentNumber, r.template_id as templateId, r.template_type_id as templateTypeId, r.company_code as companyCode, r.credit_amount as creditAmount, r.debit_amount as debitAmount, r.bill_type as billType, r.status FROM pt_fi_voucher_bill r where r.delflag=0
and r.status = #{status}
and r.account_set_id = #{accountSetId}
and r.send_status = #{sendStatus}
and r.enter_date >= #{enterDateBegain}
and r.enter_date <= #{enterDateEnd}
and (r.template_type_id = #{templateTypeId} or r.template_parent_type_id = #{templateTypeId})
order by r.enter_date desc
limit #{start},#{limit}

		select count(r.id) FROM pt_fi_voucher_bill r 
		where r.delflag=0 

             and   r.status = #{status}


             and   r.account_set_id = #{accountSetId}


             and   r.send_status = #{sendStatus}


             and    r.enter_date  >= #{enterDateBegain} 


             and    r.enter_date  <= #{enterDateEnd} 


             and   (r.template_type_id = #{templateTypeId} or r.template_parent_type_id = #{templateTypeId})
