mapper.select:
		SELECT count(1) FROM pt_sys_base_custom_form s
		where s.delflag='0' 

            and  s.code = #{code,jdbcType=VARCHAR}  


            and  s.id != #{id,jdbcType=VARCHAR}

		SELECT count(1) FROM pt_sys_base_custom_form s 
		where s.delflag='0'  

            and  s.name = #{name,jdbcType=VARCHAR}  


            and  s.id != #{id,jdbcType=VARCHAR}  


            and  s.parent_id = #{parentId,jdbcType=VARCHAR}
