mapper.select:
SELECT id, title , code, send_date sendDate,login_name loginName, user_id userId, is_open isOpen, user_name userName, msg_type msgType, op_type opType,source, app_code appCode, now() serverDate, business_id businessId, ip_address ipAddress, extend_info extendInfo, url,mobible_url mobibleUrl, deal_date dealDate, timestampdiff(HOUR, send_date, IFNULL( deal_date, now())) hourSum, mobible_param mobibleParam FROM pt_flow_sys_notice_msg WHERE delflag =0
AND login_name = #{loginName}
AND title LIKE concat('%',#{keyword},'%')
AND login_name in
#{item}
AND app_code = #{appCode}
AND first_type NOT IN ('TASKTODO','MEETING','SCHEDULE','NEWS','DIRECTORS','CLOCKIN','PENDING','TOREAD') AND op_type in ('DB','DY') AND first_type IS NOT NULL
AND first_type in('TASKTODO','PENDING','TOREAD') AND op_type in ('DB','DY') AND first_type IS NOT NULL
AND first_type = #{firstType} AND op_type in ('DB','DY') AND first_type IS NOT NULL
AND op_type in ('YB','YY')
AND op_type = #{dataType}
AND send_date >= date_sub(now(),interval 7 day)
AND send_date >= date_sub(now(),interval 30 day)
AND send_date >= date_sub(now(),interval 90 day)
ORDER BY ${sidx} ${sord}
ORDER BY send_date
ORDER BY ${sidx} ${sord}
ORDER BY send_date DESC
limit #{start}, #{limit}

mapper.update:
	UPDATE PT_FLOW_SYS_NOTICE_MSG 
	SET delflag = 1, deal_date = now(), update_date=now() 
	WHERE delflag = 0 

	    AND op_type = #{opType} 



	    AND business_id = #{businessId}  



	    AND user_id = #{currentUserId}  



	    AND op_type in 

            #{item} 




		AND user_id = #{currentUserId}



		AND user_id in 

            #{item} 




		AND url LIKE  concat('%',#{instanceIdText},'%')

		UPDATE PT_FLOW_SYS_NOTICE_MSG 
		SET delflag = '1',update_date=now()

			,manual_set = #{manualSet}


			,update_person_id = #{operatorId}


			,update_person_name = #{operatorName}

		WHERE id in 

            #{item}

UPDATE PT_FLOW_SYS_NOTICE_MSG 
SET op_type = #{newStatus}, deal_date = now()

	,first_type =#{firstType}


	,delflag =#{delflag}


	,manual_set = #{manualSet}


	,update_person_id = #{operatorId}


	,update_person_name = #{operatorName}

WHERE  delflag = 0 and op_type = #{oldStatus}

    AND id = #{id}  


    AND business_id = #{businessId}  


    AND extend_info = #{extendInfo}  



	AND user_id = #{userId}



	AND url LIKE  concat('%',#{instanceIdText},'%')

		UPDATE pt_flow_sys_notice_msg 
		SET 
		first_type = CASE WHEN op_type = 'DB' 
		THEN 'DONE' 
		WHEN op_type = 'DY' 
		THEN 'HAVEREAD' 
		ELSE first_type END,
		op_type = CASE WHEN op_type = 'DB' 
		THEN 'YB' 
		WHEN op_type = 'DY' 
		THEN 'YY' 
		ELSE op_type END,
		deal_date = now()

			,manual_set = #{manualSet}


			,update_person_id = #{operatorId}


			,update_person_name = #{operatorName}

		WHERE  delflag = 0
	    AND id in 

            #{item} 


		    AND business_id = #{businessId}  


		    AND extend_info = #{extendInfo}  



			AND user_id = #{userId}



			AND url LIKE  concat('%',#{instanceIdText},'%')

mapper.select:
select id, title,user_id, now() serverDate, send_date sendDate, timestampdiff(hour, send_date, now()) hourSum
from pt_flow_sys_notice_msg where delflag=0 and op_type='DB'
AND timestampdiff(hour, send_date, now())>24

	AND user_id = #{userId} 


    AND login_name = #{loginName}  


    AND msg_type = #{msgType}

SELECT msg.user_id userId, u.login_name loginName, count(msg.id) toDoSum 
FROM pt_flow_sys_notice_msg msg, pt_sys_org_user u
WHERE msg.user_id = u.id and msg.delflag=0 AND msg.deal_date is null 

    AND msg.op_type = #{opType}  


    AND msg.msg_type = #{msgType}  


    AND msg.app_code != #{excludeAppCode}  

group by msg.user_id, u.login_name

SELECT id, title , code, send_date sendDate,login_name loginName, user_id userId, is_open isOpen,
user_name userName, msg_type msgType, op_type opType,source, app_code appCode, now() serverDate,
business_id businessId, ip_address ipAddress, extend_info extendInfo, url,  deal_date dealDate,
timestampdiff(HOUR, send_date, IFNULL( deal_date, now())) hourSum
FROM pt_flow_sys_notice_msg  WHERE delflag =0

	AND login_name = #{loginName}


	AND title LIKE concat('%',#{title},'%')


	AND app_code = #{appCode}


	AND user_id = #{userId}


	AND msg_type = #{msgType}


	AND user_name = #{userName}


	AND business_id = #{businessId}


	AND id = #{id}



		AND first_type NOT IN ('TASKTODO','MEETING','SCHEDULE','NEWS','DIRECTORS','CLOCKIN','PENDING','TOREAD')
		AND op_type in ('DB','DY')
		AND first_type IS NOT NULL




				AND first_type in('TASKTODO','PENDING','TOREAD')
				AND op_type in ('DB','DY')
				AND first_type IS NOT NULL



					AND first_type = #{firstType}
					AND op_type in ('DB','DY')
					AND first_type IS NOT NULL








	AND op_type in ('YB','YY')



	AND op_type = #{dataType}



	AND op_type in ('DB','DY')




	AND send_date >= date_sub(now(),interval 7 day)


	AND send_date >= date_sub(now(),interval 30 day)


	AND send_date >= date_sub(now(),interval 90 day)

ORDER BY send_date DESC

	LIMIT #{start}, #{limit}

SELECT msg.id as id, msg.title, msg.login_name as loginName, msg.user_id as userId, msg.user_name as userName, msg.op_type as opType, msg.source as source, msg.app_code as appCode, msg.business_id as businessId, msg.send_date as sendDate, IFNULL(msg.deal_date,now()) as dealDate, url as url FROM pt_flow_sys_notice_msg msg WHERE msg.delflag = 0 and msg.send_date BETWEEN #{startDate} AND #{endDate} and url like '%instanceId%'
and msg.source=#{source}
and msg.appCode=#{appCode}
and op_type in ('DB','YB')

SELECT ac.`name` as acName, b.name as busiObjectName, fl.name as flowTemplate, org.prefix_name as orgName, org.id as orgId, org.type, org.parent_id as pid, i.name as instanceName, i.create_person_name as startUserName, i.flow_business_company_name as startCompanyName, i.flow_business_dept_name as startDeptName FROM pt_flow_instance_ac ac LEFT JOIN pt_flow_approve_type atype ON ( ac.approve_type_id = atype. CODE ) LEFT JOIN pt_flow_instance_post p ON ( ac.id = p.ac_id AND p.delflag = 0 ) LEFT JOIN pt_flow_instance_group g ON ( p.id = g.ac_post_id AND g.delflag = 0 ) LEFT JOIN pt_flow_instance_task t ON ( g.id = t.group_id AND t.delflag = 0 ) LEFT JOIN pt_flow_instance i ON (ac.fi_id = i.id) LEFT JOIN pt_flow_fl fl ON i.fl_id = fl.id LEFT JOIN pt_flow_instance_cs cs ON ( cs.ac_id = ac.id AND cs.fi_id = ac.fi_id AND cs.delflag = 0 ) LEFT JOIN pt_flow_business_object b ON ( b.id = i.business_object_id AND b.delflag = 0 ) LEFT JOIN pt_sys_res_app a ON ( a.id = i.app_id AND a.delflag = 0 ) LEFT JOIN pt_sys_org_post post on (p.post_id = post.id AND post.delflag = 0) LEFT JOIN pt_sys_org_orgnazation org on (post.ref_id = org.id AND org.delflag = 0) WHERE ac.ac_type > 1
and ac.fi_id = #{instanceId}
AND g.participant_id = #{userId}
AND b.id = #{busiObjectId}
