mapper.select:
SELECT t.id, u.id As entryUserId, u.user_id As userId, u.tend_id As userTendId, t.name, t.code, t.parent_id AS parentId, t.icon, t.is_inner AS isInner, t.resource_id AS resourceId, t.resource_name AS resourceName, t.url, t.status, t.sort, t.create_date as createDate, t.update_date as updateDate, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.concurrency_version as concurrencyVersion, t.tend_id as tendId, t.delflag FROM oa_sys_quick_entry_user u LEFT JOIN oa_sys_quick_entry t ON u.entry_id=t.id WHERE 1=1
and u.user_id = #{userId}
and u.tend_id = #{tendId}
and t.status=1 and u.delflag=0 and t.delflag=0 order by t.create_date

SELECT u.id As entryUserId FROM oa_sys_quick_entry_user u LEFT JOIN oa_sys_quick_entry t ON u.entry_id=t.id WHERE 1=1
and u.user_id = #{userId}
and u.tend_id = #{tendId}
and (t.parent_id = #{entryId} or t.id = #{entryId})
and t.status=1 and u.delflag=0 and t.delflag=0
