mapper.select:
select s.id as id, s.create_person_id as createPersonId, s.create_person_name as createPersonName, s.create_org_id as createOrgId, s.create_org_name as createOrgName, s.create_company_id as createCompanyId, s.create_company_name as createCompanyName, s.create_date as createDate, s.update_person_id as createPersonId, s.update_person_name as updatePersonName, s.update_date as updateDate, s.delflag as delflag, s.concurrency_version as concurrencyVersion, s.tend_id as tendId, s.name, s.description, s.type_id as typeId, s.status, s.anonymity_permission as anonymityPermission, s.publish_score as publishScore, s.reply_score as replyScore, s.set_essence_score as setEssenceScore, s.forum_manager as forumManager, s.forum_manager_id as forumManagerId, s.sort_num as sortNum, b.name as forumTypeName from OA_BBS_FORUM s left join oa_bbs_forum_type b on s.type_id=b.id where s.delflag='0' and b.delflag='0'
and s.name LIKE CONCAT('%',#{name},'%' )
order by s.create_date desc limit #{start},#{limit}
