mapper.select:
		 	  select id,stock_house_id as stockHouseId, stock_num as stockNum,stock_name as stockName,stock_category as stockCategory from oa_office_info


                   where stock_house_id = #{_parameter,jdbcType=VARCHAR}


         		group by stock_house_id;

SELECT a.id as id, a.accout_id as accoutId, a.stock_house_id as stockHouseId, a.stock_num as stockNum, a.stock_name as stockName, a.stock_specifications as stockSpecifications, a.metering_unit as meteringUnit, a.stock_brand as stockBrand, a.tend_id as tendId, a.stock_count as stockCount, a.out_stock_count as outStockCount, a.buy_date as buyDate, a.min_count as minCount, a.warn_flag as warnFlag, a.house_id as houseId, a.price as price, a.create_date AS createDate, a.update_date AS updateDate, a.create_person_id AS createPersonId, a.create_person_name AS createPersonName, a.update_person_id AS updatePersonId, a.update_person_name AS updatePersonName, a.create_org_id AS createOrgId, a.create_org_name AS createOrgName, a.create_company_id AS createCompanyId, a.create_company_name AS createCompanyName, a.concurrency_version AS concurrencyVersion, a.delflag AS delflag, h.type_name as typeName FROM oa_office_info a LEFT JOIN oa_office_house h on a.stock_house_id=h.id where a.delflag=0 and h.delflag=0
and a.stock_house_id=#{stockHouseId}
and a.accout_id=#{accoutId}
and a.stock_name like CONCAT(CONCAT('%',#{officeName}),'%')
and h.type_name like CONCAT(CONCAT('%',#{officeType}),'%')
and a.stock_num like CONCAT(CONCAT('%',#{officeNum}),'%')
order by a.create_date desc limit #{start},#{limit}

select count(a.id) from oa_office_info a   LEFT JOIN oa_office_house  h on a.stock_house_id=h.id where a.delflag=0 and h.delflag=0	   

	and a.stock_house_id=#{stockHouseId}


	 and a.accout_id=#{accoutId}


and a.stock_name  like   CONCAT(CONCAT('%',#{officeName}),'%')


and h.type_name  like  CONCAT(CONCAT('%',#{officeType}),'%')


and a.stock_num like  CONCAT(CONCAT('%',#{officeNum}),'%')

		select h.id id,h.type_name typeName,count(i.id) num from oa_office_info i left join OA_OFFICE_HOUSE h on i.stock_house_id = h.id 


		         where i.stock_house_id = #{stockHouseId,jdbcType=VARCHAR}


  		group by h.type_name,h.id;

mapper.update:
update oa_office_info t set 

   t.stock_count=#{stockCount}


    t.out_stock_count=#{outStockCount}


    t.out_stock_count=#{backOutStockCount}

  where  t.id=#{officeinfoId}
