mapper.select:
		select count(t.id) from oa_office_out t  where t.delflag=0

          and  t.theme like  CONCAT(CONCAT('%',#{name}),'%')


          and  t.status=#{status}


			and t.create_person_id = #{userId}

SELECT t.id, t.status, t.theme, t.remark, t.code, t.propose_user_id as proposeUserId, t.propose_user as proposeUser, t.propose_dept_id as proposeDeptId, t.propose_dept as proposeDept, t.instance_id as instanceId, t.create_date AS createDate, t.update_date AS updateDate, t.create_person_id AS createPersonId, t.create_person_name AS createPersonName, t.update_person_id AS updatePersonId, t.update_person_name AS updatePersonName, t.create_org_id AS createOrgId, t.create_org_name AS createOrgName, t.create_company_id AS createCompanyId, t.create_company_name AS createCompanyName, t.concurrency_version AS concurrencyVersion, t.delflag AS delflag FROM oa_office_out t WHERE t.delflag = 0
and t.theme like CONCAT(CONCAT('%',#{name}),'%')
and t.status=#{status}
and t.create_person_id = #{userId}
order by t.create_date desc limit #{start},#{limit}
