mapper.select:
select s.id, s.period_proceeding as periodProceeding, s.content, s.type, s.repetition_frequency as repetitionFrequency, s.begin_time as beginTime, s.end_time AS endTime, s.period_begin_time as periodBeginTime, s.period_end_time as periodEndTime, s.period_task_num as periodTaskNum, s.task_owner as taskOwner, s.task_owner_id as taskOwnerId, s.frequency_type as frequencyType, s.status, s.source, s.create_date as createDate, s.update_date as updateDate, s.create_person_id as createPersonId, s.create_person_name as createPersonName, s.update_person_id as updatePersonId, s.update_person_name as updatePersonName, s.create_org_id as createOrgId, s.create_org_name as createOrgName, s.create_company_id as createCompanyId, s.create_company_name as createCompanyName, s.concurrency_version as concurrencyVersion, s.delflag as delflag, s.tend_id as tendId from oa_schedule_work_schedule s WHERE 1=1 AND s.delflag = '0' AND task_owner_id = #{taskOwnerId}
AND str_to_date(s.end_time,'%Y-%c-%d %H:%i:%s') >= #{beginTime}
AND str_to_date(s.begin_time,'%Y-%c-%d %H:%i:%s') <= #{endTime}

SELECT DISTINCT
	LEFT (asws.begin_time, 19) beginTime,
	LEFT (asws.end_time, 19) endTime,
	type,
	status
FROM
	oa_schedule_work_schedule asws
WHERE
	asws.delflag = '0'
AND asws.task_owner_id = #{taskOwnerId}
AND (
	LEFT (asws.begin_time, 7) = #{month}
	OR LEFT (asws.end_time, 7) = #{month}
);
