mapper.select:
select a.id as id, a.servey_id as serveyId, a.question as question, a.question_num as questionNum, a.question_tip as questionTip, a.question_score as questionScore, a.question_required as questionRequired, a.question_type_code as questionTypeCode, b.id as subQuestionId, b.question_content as questionContent, b.question_code as questionCode, c.id as optionId, c.sequence_num as sequenceNum, c.description as description, CASE WHEN c.weight_factor is null then '0' ELSE c.weight_factor END as weightFactor, c.option_type as optionType from oa_servey_question a LEFT JOIN oa_servey_sub_question b on a.id=b.question_id LEFT JOIN oa_survey_question_option c on a.id=c.question_id where 1=1
and a.id= #{id}
and a.name like CONCAT(CONCAT('%',#{name}),'%')
and a.servey_id = #{serveyId}
order by a.question_num,b.question_code,c.sequence_num
limit #{start},#{limit}

SELECT count(a.id) FROM oa_servey_question a where 1=1
and a.id= #{id}
and a.name like CONCAT(CONCAT('%',#{name}),'%')
and a.servey_id = #{serveyId}
