mapper.select:
SELECT t.id, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.create_date as createDate, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.update_date as updateDate, t.delflag as delflag, t.tend_id as tendId, t.concurrency_version as concurrencyVersion,
s.name as serveyName, s.thru_date as thruDate, s.id as serveyId, t.user_id as userId, t.resonse_date as resonseDate, c.name as categoryName, CASE WHEN s.thru_date<NOW() THEN '已结束' WHEN s.from_date>NOW() THEN '未开始' ELSE '进行中' END as status FROM oa_servey s left join oa_servey_response t on s.id = t.servey_id join oa_servey_party p on s.id = p.servey_id left join oa_servey_category c on s.servey_category_id = c.id where 1=1 and (p.type ='READER' or p.type ='NOTICE')
AND p.party_id = #{createPersonId}
AND s.name like CONCAT(CONCAT('%',#{name}),'%')
group by s.id order by s.thru_date desc
limit #{start},#{limit}

SELECT count(s.id) FROM oa_servey s left join oa_servey_response t on s.id = t.servey_id join oa_servey_party p on s.id = p.servey_id left join oa_servey_category c on s.servey_category_id = c.id where 1=1 and (p.type ='READER' or p.type ='NOTICE')
AND p.party_id = #{createPersonId}
AND s.name like CONCAT(CONCAT('%',#{name}),'%')
