mapper.select:
SELECT T.SORT AS SORT FROM OA_SYS_QUICK_ENTRY T WHERE 1 = 1
AND T.parent_id = #{parentId,jdbcType=VARCHAR}
AND T.SORT = #{sort,jdbcType=VARCHAR}
ORDER BY T.SORT DESC LIMIT 0,1

SELECT t.id, t.name, t.code, t.parent_id AS parentId, t.icon AS linkIcon, t.is_inner AS isInner, t.resource_id AS resourceId, t.resource_name AS resourceName, t.url, t.status, t.sort, t.create_date as createDate, t.update_date as updateDate, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.concurrency_version as concurrencyVersion, t.delflag from OA_SYS_QUICK_ENTRY t where t.delflag=0
and t.parent_id= #{_parameter,jdbcType=VARCHAR}
order by t.sort
