mapper.select:
SELECT id, create_person_id createPersonId, create_person_name createPersonName, create_org_id createOrgId, create_org_name createOrgName, create_company_id createCompanyId, create_company_name createCompanyName, create_date createDate, update_person_id updatePersonId, update_person_name updatePersonName, update_date updateDate, delflag, concurrency_version concurrencyVersion, NAME name, CODE code, active, simple_trigger simpleTrigger, time_interval timeInterval, end_time endTime, type, fully_qualified_name fullyQualifiedName, method_name methodName, time_expression timeExpression, remark, tend_id tendId, app_id appId, is_for_platform isForPlatform FROM pt_univ_task_info
id IN
#{id}

SELECT id, create_person_id as createPersonId, create_person_name as createPersonName, create_org_id as createOrgId, create_org_name as createOrgName, create_company_id as createCompanyId, create_company_name as createCompanyName, create_date as createDate, update_person_id as updatePersonId, update_person_name as updatePersonName, update_date as updateDate, delflag, concurrency_version as concurrencyVersion, NAME, CODE, active, simple_trigger as simpleTrigger, time_interval as timeInterval, start_time as startTime, end_time as endTime, TYPE, fully_qualified_name as fullyQualifiedName, method_name as methodName, trigger_object_name as triggerObjectName, job_object_name as jobObjectName, time_expression as timeExpression, remark, tend_id as tendId, app_id as appId, is_for_platform as isForPlatform FROM pt_univ_task_info where 1=1
and name = #{name}
order by create_date desc limit #{start},#{limit}
