mapper.select:
SELECT t.id as id, t.name as name, t.code as code, t.province_id as provinceId, t.city_id as cityId, t.address as address, t.relation_person as relationPerson, t.phone as phone, t.finance_code as financeCode, t.message_info as messageInfo, t.message_type as messageType, t.license as license, t.representative as representative, t.registration_date as registrationDate, t.company_address as companyAddress, t.remark as remark, t.disabled_id as disabledId, t.disabled_date as disabledDate, t.work_phone as workPhone, t.supplier_resoure as supplierResoure, t.supplier_app as supplierApp, t.status as status,
t.remark as remark, t.create_date as createDate, t.update_date as updateDate, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.concurrency_version as concurrencyVersion, t.delflag as delflag
from pt_sys_base_supplier t where id=#{id}

SELECT t.id AS id, t. `NAME` AS `name`, t. `CODE` AS `code`, t.relation_person AS relationPerson, t.phone AS phone, t.representative AS representative, t.finance_code AS financeCode, t.message_info AS messageInfo, t. `STATUS` AS `status`, t.company_id as companyId, r. NAME AS provinceName, rt. NAME AS cityName FROM pt_sys_base_supplier t LEFT JOIN pt_sys_base_region r ON r.id = t.province_id LEFT JOIN pt_sys_base_region rt ON rt.id = t.city_id LEFT JOIN pt_sys_base_supplier_company c on c.supper_id = r.id WHERE t.delflag = 0
and t.name like CONCAT(CONCAT('%',#{name}),'%')
and t.company_id like CONCAT(CONCAT('%',#{companyname}),'%')
and t.province_id =#{provicename}
ORDER BY t.create_date DESC limit #{start},#{limit}

SELECT t.id as id, t.name as name, t.code as code, t.province_id as provinceId, t.city_id as cityId, t.address as address, t.relation_person as relationPerson, t.phone as phone, t.finance_code as financeCode, t.message_info as messageInfo, t.message_type as messageType, t.license as license, t.representative as representative, t.registration_date as registrationDate, t.company_address as companyAddress, t.remark as remark, t.disabled_id as disabledId, t.disabled_date as disabledDate, t.work_phone as workPhone, t.supplier_resoure as supplierResoure, t.supplier_app as supplierApp, t.status as status, t.remark as remark, t.create_date as createDate, t.update_date as updateDate, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.concurrency_version as concurrencyVersion, t.delflag as delflag FROM pt_sys_base_supplier t WHERE t.delflag = 0 and t.id != #{id}
and code = #{code}
and name = #{name}
