mapper.select:
		SELECT s.id, s.name ,s.code,s.parent_id as pId,s.status FROM pt_sys_base_custom_archives_item s 
		where s.delflag='0' and s.main_id=#{mainId}

			and s.id not in 

	            #{item} 



            and  s.status = #{status,jdbcType=VARCHAR}  


		 order by sort

		SELECT count(1) FROM pt_sys_base_custom_archives_item s
		where s.delflag='0'  and main_id=#{mainId,jdbcType=VARCHAR}

            and  s.code = #{code,jdbcType=VARCHAR}  


            and  s.id != #{id,jdbcType=VARCHAR}

		SELECT count(1) FROM pt_sys_base_custom_archives_item s 
		where s.delflag='0'  and main_id=#{mainId,jdbcType=VARCHAR}  

            and  s.name = #{name,jdbcType=VARCHAR}  


            and  s.id != #{id,jdbcType=VARCHAR}

select s.id as id, s.create_person_id as createPersonId, s.create_person_name as createPersonName, s.create_org_id as createOrgId, s.create_org_name as createOrgName, s.create_company_id as createCompanyId, s.create_company_name as createCompanyName, s.create_date as createDate, s.update_person_id as createPersonId, s.update_person_name as updatePersonName, s.update_date as updateDate, s.delflag as delflag, s.concurrency_version as concurrencyVersion, s.tend_id as tendId, s.code, s.name, s.parent_id as parentId, s.main_id as mainId, s.sort, s.description, s.status from pt_sys_base_custom_archives_item s where s.delflag='0'
and ( s.parent_id =#{parentId} or s.id=#{parentId} )
and s.main_id =#{mainId}
order by s.sort limit #{start},#{limit}

select count(1) from pt_sys_base_custom_archives_item s where s.delflag='0'
and ( s.parent_id =#{parentId} or s.id=#{parentId} )
and s.main_id =#{mainId}
