mapper.select:
SELECT t.id, t.delflag, t.login_name loginName, t.`name`, t.com_id comId, t.com_name comName, t.login_ip loginIp, t.login_browser loginBrowser, t.operation_time operationTime, t.operation_type_id operationTypeId, t.sys_code sysCode, t.node, SUBSTR(t.note,1,100) note, if(LENGTH(t.note)>100,1,0) ifLong FROM pt_sys_log_operation t WHERE 1 = 1
AND t.operation_type_id = #{opeTypeId}
AND t.sys_code in (#{sysCode},#{sysId})
AND ( t.`name` LIKE CONCAT('%', #{uName}, '%') OR t.login_name LIKE CONCAT('%', #{uName}, '%') )
AND t.create_date >= #{startTime}
AND t.create_date <= #{endTime}
order by t.${sidx}
${sord}
limit #{start},#{limit}

SELECT count(operation_type_id) FROM pt_sys_log_operation t WHERE 1 = 1
AND t.sys_code in (#{sysCode} ,#{sysId})
AND t.operation_type_id = #{opeTypeId}
AND ( t.`name` LIKE CONCAT('%', #{uName}, '%') OR t.login_name LIKE CONCAT('%', #{uName}, '%') )
AND t.create_date >= #{startTime}
AND t.create_date <= #{endTime}

SELECT a.id,a.`code`,a.`name` from pt_sys_res_app a WHERE a.`code` in

	 #{code}

 or a.id in

	#{id}
