mapper.select:
SELECT t.id, t.sys_code sysCode, t.task_code taskCode, t.task_name taskName, t.start_time startTime, t.end_time endTime, t.execute_time executeTime, t.run_info runInfo, t.execute_status executeStatus, a.`name` appName, a.id appId FROM pt_sys_log_task t LEFT JOIN pt_sys_res_app a on a.`code`=t.sys_code and a.delflag=0 WHERE 1 = 1
AND t.sys_code =#{sysCode}
AND t.execute_status =#{resFlag}
AND t.start_time >= #{startTime}
AND t.end_time <= #{endTime}
AND (t.task_code LIKE CONCAT('%',#{taskName},'%') or t.task_name LIKE CONCAT('%',#{taskName},'%'))
order by ${sidx}
${sord}
limit #{start},#{limit}

SELECT
	count(*)
FROM
	pt_sys_log_task t
WHERE
	1 = 1

	AND t.sys_code =#{sysCode}


	AND t.execute_status =#{resFlag}


	AND t.start_time >= #{startTime}


	AND t.end_time <= #{endTime}


	AND (t.task_code LIKE CONCAT('%',#{taskName},'%') or t.task_name LIKE CONCAT('%',#{taskName},'%'))
