mapper.select:
SELECT t.id, t.code, t.name, t.bill_id as billId, t.sort, t.create_date as createDate, t.update_date as updateDate, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.concurrency_version as concurrencyVersion, t.tend_id as tendId, t.delflag FROM pt_sys_num_form_variable t where t.delflag=0
and ( t.code like CONCAT(CONCAT('%',#{code}),'%') or t.name like CONCAT(CONCAT('%',#{name}),'%') )
and t.bill_id = #{billId,jdbcType=VARCHAR}
order by t.sort
limit #{start},#{limit}

SELECT count(t.id) FROM pt_sys_num_form_variable t where t.delflag=0
and ( t.code like CONCAT(CONCAT('%',#{code}),'%') or t.name like CONCAT(CONCAT('%',#{name}),'%') )
and t.bill_id = #{billId,jdbcType=VARCHAR}
