mapper.select:
select id,name,sort,parent_id as 'parentId',type,root_id AS rootId,`status` AS status ,prefix_id AS prefixId ,prefix_name AS prefixName  from pt_sys_org_orgnazation 
where root_id = #{_parameter,jdbcType=VARCHAR} 
and (parent_id = '' or parent_id is null) 
and (type = 'zb' or type = 'company') ORDER BY sort;

		select id,name,sort,parent_id as 'parentId',type,root_id AS rootId,`status` AS status ,prefix_id AS prefixId ,prefix_name AS prefixName  from pt_sys_org_orgnazation 


                        where parent_id = #{_parameter,jdbcType=VARCHAR} ORDER BY sort;


                        where parent_id is null ORDER BY sort;

select id,name,sort,parent_id as 'parentId',type,root_id AS rootId,`status` AS status ,prefix_id AS prefixId ,prefix_name AS prefixName  
 from pt_sys_org_orgnazation
where 1=1

		and `status` =#{orgStatus}


		and delflag=#{orgDelFlag}

order by  case when sort is null then 1 else 0 end, sort , LENGTH(prefixId);

SELECT o.id AS id, o.name as name, 'org' as type,o.prefix_name prefixName FROM pt_sys_org_orgnazation o WHERE 1=1
and o.id in
#{item}

SELECT top.id AS id, top.concurrency_version AS concurrencyVersion, top.NAME AS NAME, top.full_name AS fullName, top.leader_id AS leaderId, top.up_leader_id AS upLeaderId, top.parent_id AS parentId, top.tend_id AS tendId, top.type AS type, top.root_id AS rootId, top.sort AS sort, top.icon AS icon, top.STATUS AS STATUS, top.remark AS remark, top.create_date AS createDate, top.update_date AS updateDate, top.create_person_id AS createPersonId, top.create_person_name AS createPersonName, top.update_person_id AS updatePersonId, top.update_person_name AS updatePersonName, top.create_org_id AS createOrgId, top.create_org_name AS createOrgName, top.create_company_id AS createCompanyId, top.create_company_name AS createCompanyName, top.delflag AS delflag, top.code AS code, top.prefix_id as prefixId, top.prefix_name as prefixName from pt_sys_org_orgnazation t,pt_sys_org_orgnazation top where t.id = #{orgId}
and top.type ${topType}
and t.prefix_id like concat(top.prefix_id,'%') ORDER BY LENGTH(top.prefix_id);

SELECT * from view_org_dto o WHERE 1=1

	and o.parentId=#{parentId}


	and o.id in

		#{id}



	and o.type in (${typeS})


	and o.type = #{type}

SELECT DISTINCT p.ref_id from pt_sys_org_post p 
	LEFT JOIN pt_sys_org_post_user pu on pu.post_id=p.id
	WHERE p.delflag=0 and pu.user_id=#{userId}

SELECT * from view_org_dto o WHERE 1=1

	and o.id in

		#{id}



	and o.type ${orgType}


	and o.`name` LIKE CONCAT('%',#{keyWord},'%')

SELECT o.id from pt_sys_org_orgnazation o
	LEFT JOIN pt_sys_org_orgnazation t on o.prefix_id LIKE CONCAT('',t.prefix_id,'/%') or t.id=o.id
	 where o.delflag=0 and o.`status`=1 and t.id in

		 #{id}

	 and o.type ${orgType}

SELECT s.id,s.`code`,CONCAT(p.`name`,'/',s.`name`) `name`,s.parent_id parentId from pt_sys_org_orgnazation s LEFT JOIN pt_sys_org_orgnazation p on s.parent_id=p.id
LEFT JOIN pt_sys_org_orgnazation f on s.prefix_id like CONCAT(f.prefix_id,'/%') or f.id=s.id
WHERE s.delflag =0 and s.`status`=1
and f.id=#{companyId}
and s.type = #{type}
and s.type in ('${typeS}')

SELECT t.* FROM
	(SELECT f.id,f.`name`,f.type,f.prefix_id,f.prefix_name,o.id Sid  FROM pt_sys_org_orgnazation o 
	LEFT JOIN pt_sys_org_orgnazation f on o.prefix_id like CONCAT(f.prefix_id,"%")
	WHERE o.type ='dept' and o.id in 

		#{orgId}

	ORDER BY LENGTH(f.prefix_id)
	${orderBy}
	) t 
GROUP BY t.type,t.Sid
ORDER BY t.sid,t.type

SELECT f.id,f.`name`,f.type,f.prefix_id,f.prefix_name,o.id Sid FROM pt_sys_org_orgnazation o LEFT JOIN pt_sys_org_orgnazation f on o.prefix_id like CONCAT(f.prefix_id,"%") WHERE o.type ='dept' and o.id in
#{orgId}
ORDER BY o.id,LENGTH(f.prefix_id) DESC

SELECT o.* from view_org_dto o
LEFT JOIN pt_sys_org_user u on u.belong_org_id=o.id
where u.id = #{_parameter,jdbcType=VARCHAR}
