SELECT GROUP_CONCAT(po.`code`) from pt_sys_res_data_permission p LEFT JOIN pt_sys_res_data_point po on po.id=data_point_id LEFT JOIN pt_sys_res_data_item i on i.id=po.item_id WHERE i.item_code=#{itemCode} and po.delflag=0 and i.delflag=0
and i.app_id=#{appId}
and p.role_id in
#{roleId}

select DISTINCT t.id id,t.`code` code ,t.`name` name,t.url url ,tt.`name` appName,tt.`code` appCode,ttt.`name` menuName,ttt.`code` menuCode from pt_sys_res_operation t LEFT JOIN pt_sys_res_func_permission f on t.id = f.operation_id LEFT JOIN pt_sys_res_app tt on t.app_id = tt.id LEFT JOIN pt_sys_res_resource ttt on t.resource_id = ttt.id where t.delflag = '0' and tt.`status` = '1' and tt.delflag = '0' and ttt.`status` = '1' and ttt.delflag ='0'
and f.role_id IN
#{item}
and tt.`code` = #{appCode}
and ttt.`code` = #{menuCode}

select DISTINCT t.id id ,t.code code ,t.name name ,t.url url ,a.code appCode ,a.`name` appName from pt_sys_res_resource t LEFT JOIN pt_sys_res_operation o ON t.id = o.resource_id LEFT JOIN pt_sys_res_func_permission f on o.id = f.operation_id LEFT JOIN pt_sys_res_app a on t.app_id = a.id where o.delflag = '0' and t.`status` = '1' and t.delflag = '0' and a.`status` = '1' and a.delflag ='0'
and f.role_id IN
#{item}
and a.`code` = #{appCode}
