mapper.select:
select t.concurrency_version as concurrencyVersion, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.create_date as createDate, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.delflag, t.id, t.prefix_id as prefixId, t.tend_id as tendId, t.update_date as updateDate, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.account_set_id as accountSetId, t.ass_ids as assIds, t.ass_names as assNames, t.biz_item_ids as bizItemIds, t.biz_item_names as bizItemNames, t.code, t.name, t.sort, t.parent_id as parentId FROM pt_fi_account_caption t where t.delflag=0
and t.account_set_id =#{accountSetId}
and t.prefix_id like CONCAT(CONCAT('%',#{prefixId}),'%')
order by t.sort;

select t.concurrency_version as concurrencyVersion, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.create_date as createDate, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.delflag, t.id, t.prefix_id as prefixId, t.tend_id as tendId, t.update_date as updateDate, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.account_set_id as accountSetId, t.ass_ids as assIds, t.ass_names as assNames, t.biz_item_ids as bizItemIds, t.biz_item_names as bizItemNames, t.code, t.name, t.sort, t.parent_id as parentId FROM pt_fi_account_caption t where t.delflag=0
and t.account_set_id =#{accountSetId}
and FIND_IN_SET(#{bizItemIds},t.biz_item_ids)
order by t.sort;
