mapper.select:
SELECT t.id, t.code, t.name, t.type, t.parent_id as parentId, t.biz_object_id as bizObjectId, t.is_query isQuery, t.display, t.url_type_flag as urlTypeFlag, t.create_date as createDate, t.update_date as updateDate, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.concurrency_version as concurrencyVersion, t.delflag as delflag from pt_fi_business_field t where t.delflag=0
and t.biz_object_id= #{_parameter,jdbcType=VARCHAR}
order by t.sort

SELECT e.name AS parentName, t.id, t.code, t.name, t.name nodeIcon, t.type, t.parent_id as parentId, t.biz_object_id as bizObjectId, t.is_query isQuery, t.display, t.url_type_flag as urlTypeFlag, t.sort, t.create_date as createDate, t.update_date as updateDate, t.create_person_id as createPersonId, t.create_person_name as createPersonName, t.update_person_id as updatePersonId, t.update_person_name as updatePersonName, t.create_org_id as createOrgId, t.create_org_name as createOrgName, t.create_company_id as createCompanyId, t.create_company_name as createCompanyName, t.concurrency_version as concurrencyVersion, t.delflag FROM pt_fi_business_field t LEFT JOIN pt_fi_business_field e ON t.parent_id = e.id where 1=1
and t.parent_id = #{parentId}
and t.parent_id='0'
and t.biz_object_id = #{bizObjectId}
and t.delflag=0 order by t.sort
