mapper.update:
UPDATE `pt_task` a SET a.`task_status`
= #{taskStatus}
WHERE 1=1 AND a.`delflag` = '0'

	AND a.`task_id` = #{taskId}

UPDATE `pt_task` a SET a.`isfollow`
= #{isfollow}
WHERE a.`task_id` = #{taskId} AND a.`delflag` = '0'

mapper.select:
SELECT `id`, `task_id` AS taskId, `task_name` AS taskName, `task_status` AS taskStatus, `task_liable_id` AS taskLiableId, `task_liable_name` AS taskLiableName, `begin_date` AS beginDate, `end_date` AS endDate, `task_progress` AS taskProgress, `task_urgent_flag` AS taskUrgentFlag, `task_describe` AS taskDescribe, `advance_time` AS advanceTime, `remind_time` AS remindTime, `project_id` AS projectId, `projectstage_id` AS projectstageId, `remind_id` AS remindId, `create_person_id` AS createPersonId, `create_person_name` AS createPersonName, `create_date` AS createDate, `update_person_id` AS updatePersonId, `update_person_name` AS updatePersonName, `update_date` AS updateDate, `delflag`, `isfollow` FROM `pt_task` WHERE delflag = '0'
AND task_id = #{taskId}
AND remind_id = #{remindId}

SELECT `id`, `task_id` AS taskId, `task_name` AS taskName, `task_status` AS taskStatus, `task_liable_id` AS taskLiableId, `task_liable_name` AS taskLiableName, `begin_date` AS beginDate, `end_date` AS endDate, `task_progress` AS taskProgress, `task_urgent_flag` AS taskUrgentFlag, `task_describe` AS taskDescribe, `advance_time` AS advanceTime, `remind_time` AS remindTime, `project_id` AS projectId, `projectstage_id` AS projectstageId, `remind_id` AS remindId, `create_person_id` AS createPersonId, `create_person_name` AS createPersonName, `create_date` AS createDate, `update_person_id` AS updatePersonId, `update_person_name` AS updatePersonName, `update_date` AS updateDate, `delflag`, `isfollow` FROM `pt_task` WHERE delflag = '0'
AND task_name like CONCAT('%',#{taskName},'%')
AND task_status = #{taskStatus}
AND project_id = #{projectId}
AND projectstage_id = #{projectstageId}
AND task_liable_id = #{taskLiableId}
AND create_person_id = #{createPersonId}
AND task_id = #{taskId}
AND remind_status = #{remindStatus}
ORDER BY end_date desc
${dynamicSql}
limit #{pageNum},#{pageCount}


SELECT
COUNT(`task_id`)
FROM
`pt_task`
WHERE delflag = '0'


	AND task_name like CONCAT('%',#{taskName},'%')



	AND task_status = #{taskStatus}



	AND project_id = #{projectId}



	AND projectstage_id = #{projectstageId}



	AND task_liable_id = #{taskLiableId}



	AND create_person_id = #{createPersonId}



	AND task_id = #{taskId} 



	ORDER BY end_date desc

SELECT `id`, `task_id` AS taskId, `task_name` AS taskName, `task_status` AS taskStatus, `task_liable_id` AS taskLiableId, `task_liable_name` AS taskLiableName, `begin_date` AS beginDate, `end_date` AS endDate, `task_progress` AS taskProgress, `task_urgent_flag` AS taskUrgentFlag, `task_describe` AS taskDescribe, `advance_time` AS advanceTime, `remind_time` AS remindTime, `project_id` AS projectId, `projectstage_id` AS projectstageId, `remind_id` AS remindId, `create_person_id` AS createPersonId, `create_person_name` AS createPersonName, `create_date` AS createDate, `update_person_id` AS updatePersonId, `update_person_name` AS updatePersonName, `update_date` AS updateDate, `delflag`, `isfollow` FROM `pt_task` WHERE delflag = '0'
AND task_name like CONCAT('%',#{taskName},'%')
AND task_status = #{taskStatus}
ORDER BY end_date desc
${dynamicSql}
limit #{pageNum},#{pageCount}

SELECT
`task_id` AS taskId, `task_name` AS taskName, `begin_date` AS beginTime, `end_date` AS endTime FROM `pt_task` WHERE delflag = '0'
AND task_liable_id = #{userId}
AND task_id = #{taskId}
AND ((begin_date <= #{beginTime} AND end_date >= #{endTime}) OR (begin_date >= #{beginTime} AND end_date <= #{endTime}) OR (begin_date >= #{beginTime} AND begin_date <= #{endTime}) OR (end_date >= #{beginTime} AND end_date <= #{endTime}))
ORDER BY end_date desc

SELECT COUNT(DATE_FORMAT(a.`begin_date`,'%Y-%m-%d')) AS taskCount,a.`begin_date` AS beginTime,a.`task_name` AS taskName FROM pt_task a WHERE a.task_liable_id = #{userId} AND a.delflag = '0'
AND a.`begin_date` BETWEEN #{beginTime} AND #{endTime}
GROUP BY DATE_FORMAT(a.`begin_date`,'%Y-%m-%d')
